import { useEffect, useState } from "react";
import axios from "axios";
import DisplayPost from "./DisplayPost";
import AxiosCreatePost from "./AxiosCreatePost";
import AxiosUpdatePost from "./AxiosUpdatePost";

const AxiosGetDemo = () => {
    const [posts, setPosts] = useState([]);

    const refreshPosts = () => {
        axios.get("http://localhost:8000/posts")
            .then(res => {
                setPosts(res.data);
            });
    };

    useEffect(() => {
        refreshPosts();
    }, []);

    return (
        <>
            <h2>Axios CRUD Demo</h2>
            <AxiosCreatePost refreshPosts={refreshPosts} />
            <AxiosUpdatePost refreshPosts={refreshPosts} />
            <DisplayPost posts={posts} refreshPosts={refreshPosts} />
        </>
    );
}

export default AxiosGetDemo;
